var COLLECTION_NAME = "salesinfo";
exports.getModel = function(mongoose){
  var schema = new mongoose.Schema({
    id: String,
    title: String,
    publisher:String,
    author:String,
    salesY:String,
    salesM:String,
    salesD:String,
  });
  mongoose.model(COLLECTION_NAME, schema);
  return mongoose.model(COLLECTION_NAME);
};
